import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const symbols = ['Σ', 'π', '∞', '∆', 'Ω', '√'];

function IntroAnimation() {
  const [showSymbols, setShowSymbols] = useState(true);
  const magicalSound = new Audio('/assets/magical-chime.mp3');

  useEffect(() => {
    const timer = setTimeout(() => {
      magicalSound.play();
      setShowSymbols(false);
    }, 3000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <motion.div
      className="fixed inset-0 flex items-center justify-center bg-midnight z-50"
      exit={{ opacity: 0 }}
      transition={{ duration: 1 }}
    >
      <div className="relative">
        {/* Merkez sembol */}
        <motion.div
          initial={{ scale: 0, rotate: 0 }}
          animate={{ scale: 1, rotate: 360 }}
          transition={{ duration: 2, ease: "easeOut" }}
          className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-6xl text-gold"
        >
          Ω
        </motion.div>

        {/* Dönen semboller */}
        <AnimatePresence>
          {showSymbols && (
            <div className="relative w-40 h-40">
              {symbols.map((symbol, index) => {
                const angle = (index * 360) / symbols.length;
                const radius = 80;
                const x = Math.cos((angle * Math.PI) / 180) * radius;
                const y = Math.sin((angle * Math.PI) / 180) * radius;

                return (
                  <motion.div
                    key={symbol}
                    initial={{ x: 0, y: 0, opacity: 0 }}
                    animate={{
                      x,
                      y,
                      opacity: 1,
                      rotate: [0, 360],
                    }}
                    exit={{ opacity: 0 }}
                    transition={{
                      duration: 2,
                      ease: "easeOut",
                      rotate: { repeat: Infinity, duration: 3 }
                    }}
                    className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-3xl text-gold-light"
                  >
                    {symbol}
                  </motion.div>
                );
              })}
            </div>
          )}
        </AnimatePresence>

        {/* Işık efekti */}
        <div className="absolute inset-0 bg-gradient-radial from-gold/20 to-transparent rounded-full filter blur-xl" />

        {/* Başlık */}
        <motion.h1
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 2 }}
          className="absolute top-full left-1/2 -translate-x-1/2 mt-8 text-4xl font-cinzel text-gold text-center whitespace-nowrap"
        >
          İsim Analizi
        </motion.h1>
      </div>
    </motion.div>
  );
}

export default IntroAnimation;
