import React, { useState } from 'react';
import { motion } from 'framer-motion';

const nameCategories = {
  modern: {
    tr: ['Defne', 'Çınar', 'Ada', 'Toprak', 'Deniz', 'Güneş', 'Rüzgar', 'Yağmur'],
    en: ['Luna', 'Nova', 'Atlas', 'River', 'Sky', 'Rain', 'Storm', 'Phoenix']
  },
  classic: {
    tr: ['Mehmet', 'Ayşe', 'Mustafa', 'Fatma', 'Ali', 'Zeynep', 'Ahmet', 'Emine'],
    en: ['James', 'Mary', 'John', 'Elizabeth', 'William', 'Sarah', 'Michael', 'Emma']
  },
  spiritual: {
    tr: ['Asya', 'Mira', 'Aras', 'İlhan', 'Umay', 'Kayra', 'Irmak', 'Kutay'],
    en: ['Aria', 'Sage', 'Zen', 'Eden', 'Soul', 'Faith', 'Hope', 'Angel']
  }
};

const letterEnergies = {
  A: 85, B: 70, C: 65, D: 75, E: 80, F: 60, G: 75, H: 65, I: 70,
  İ: 80, J: 55, K: 85, L: 60, M: 75, N: 70, O: 65, Ö: 70, P: 60,
  R: 75, S: 80, Ş: 75, T: 70, U: 65, Ü: 70, V: 60, Y: 75, Z: 80
};

const NameSuggestions = ({ language }) => {
  const [selectedCategory, setSelectedCategory] = useState('modern');
  const [hoveredName, setHoveredName] = useState(null);

  const getNameEnergy = (name) => {
    const firstLetter = name.charAt(0).toUpperCase();
    return letterEnergies[firstLetter] || 50;
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="mt-12 p-6 bg-midnight-light/30 backdrop-blur-sm rounded-lg border-2 border-gold/30"
    >
      <h2 className="text-2xl font-cinzel text-gold mb-6">
        {language === 'tr' ? 'İsim Önerileri' : 'Name Suggestions'}
      </h2>

      <div className="flex gap-4 mb-8">
        {Object.keys(nameCategories).map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-4 py-2 rounded-lg transition-colors ${
              selectedCategory === category
                ? 'bg-gold text-midnight font-medium'
                : 'bg-gold/20 text-gold hover:bg-gold/30'
            }`}
          >
            {language === 'tr'
              ? category === 'modern'
                ? 'Modern'
                : category === 'classic'
                ? 'Klasik'
                : 'Spiritüel'
              : category.charAt(0).toUpperCase() + category.slice(1)}
          </button>
        ))}
      </div>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {nameCategories[selectedCategory][language].map((name) => (
          <motion.div
            key={name}
            onHoverStart={() => setHoveredName(name)}
            onHoverEnd={() => setHoveredName(null)}
            whileHover={{ scale: 1.05 }}
            className="relative p-4 bg-midnight/50 rounded-lg border border-gold/20 hover:border-gold/40 transition-colors cursor-pointer group"
          >
            <h3 className="text-lg font-cinzel text-gold text-center mb-2">{name}</h3>
            
            {hoveredName === name && (
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="absolute inset-x-0 bottom-0 p-2 bg-midnight/90 rounded-b-lg transform translate-y-full z-10"
              >
                <div className="text-sm text-gold/80 text-center mb-1">
                  {language === 'tr' ? 'Enerji' : 'Energy'}
                </div>
                <div className="h-2 bg-midnight-light rounded-full overflow-hidden">
                  <motion.div
                    initial={{ width: 0 }}
                    animate={{ width: `${getNameEnergy(name)}%` }}
                    className="h-full bg-gradient-to-r from-gold to-gold-light"
                  />
                </div>
                <div className="text-xs text-gold/60 text-center mt-1">
                  {getNameEnergy(name)}%
                </div>
              </motion.div>
            )}
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default NameSuggestions;
