// İsim analizi fonksiyonu - İlk harfe göre analiz
export const analyzeNameByFirstLetter = (name) => {
  if (!name || name.length === 0) return null;
  
  const firstLetter = name[0].toLowerCase();
  
  // Türkçe karakter dönüşümü
  const normalizedLetter = firstLetter
    .replace('ı', 'i')
    .replace('ğ', 'g')
    .replace('ü', 'u')
    .replace('ş', 's')
    .replace('ö', 'o')
    .replace('ç', 'c');
  
  return {
    letter: firstLetter,
    characteristics: nameCharacteristics[firstLetter] || nameCharacteristics[normalizedLetter] || "Bu harf için özel bir analiz bulunmamaktadır."
  };
};

// İsim bilgisi getirme fonksiyonu - Tüm isim analizi 
export const getNameInfo = (name) => {
  const normalizedName = name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
  
  // Eğer isim veritabanında varsa detaylı bilgileri döndür
  if (turkishNames[normalizedName]) {
    return turkishNames[normalizedName];
  }
  
  // Veritabanında yoksa harflere göre analiz oluştur
  const letterAnalysis = {};
  const letters = normalizedName.split('');
  
  let numerologySum = 0;
  
  letters.forEach(letter => {
    const upperLetter = letter.toUpperCase();
    
    // Türkçe karakterler için uygun değerleri al
    let meaning = letterMeanings[upperLetter];
    if (!meaning) {
      const normalizedLetter = upperLetter
        .replace('I', 'İ')
        .replace('Ğ', 'G')
        .replace('Ü', 'U')
        .replace('Ş', 'S')
        .replace('Ö', 'O')
        .replace('Ç', 'C');
      meaning = letterMeanings[normalizedLetter] || "Bu harf için anlam bulunamadı";
    }
    
    letterAnalysis[upperLetter] = meaning;
    
    // Numeroloji değeri hesaplama, Türkçe karakterlere uygun değerleri al
    let numValue = numerologyValues[upperLetter];
    if (!numValue) {
      const normalizedLetter = upperLetter
        .replace('I', 'İ')
        .replace('Ğ', 'G')
        .replace('Ü', 'U')
        .replace('Ş', 'S')
        .replace('Ö', 'O')
        .replace('Ç', 'C');
      numValue = numerologyValues[normalizedLetter] || 0;
    }
    
    numerologySum += numValue;
  });
  
  // Numeroloji değerini tek basamaklı hale getir
  while (numerologySum > 9) {
    numerologySum = numerologySum.toString().split('').reduce((a, b) => parseInt(a) + parseInt(b), 0);
  }
  
  // Kübranur gibi isimler için anlamlı değerler
  const nameFirstLetterAnalysis = analyzeNameByFirstLetter(name);
  const nameDescription = nameFirstLetterAnalysis ? nameFirstLetterAnalysis.characteristics : "";
  
  // İsim kökeni tahmini geliştirmesi
  const lowerName = name.toLowerCase();
  let origin = "Bilinmiyor";
  
  // Köken kalıpları ve sonekler - daha detaylı
  const originPatterns = {
    "Türkçe": [
      "alp", "kaya", "can", "han", "er", "ay", "gün", "demir", "bey", "dağ", 
      "deniz", "ateş", "gök", "yıldız", "kurt", "doğan", "su", "taş", "koç", 
      "kılıç", "çelik", "kağan", "yiğit", "cihan", "özgür", "çetin", "tekin", 
      "duygu", "sevgi", "ışık", "aydın", "güneş"
    ],
    "Arapça": [
      "ahmed", "muhammed", "ali", "hasan", "hüseyin", "mustafa", "fatma", "ayşe", 
      "nur", "hatice", "abdullah", "ibrahim", "meryem", "kerim", "rahman", "zehra", 
      "emin", "halil", "harun", "selim", "salih", "cemal", "cemil", "sultan", 
      "şerif", "kemal", "celal", "arif", "hamza", "leyla"
    ],
    "Farsça": [
      "gül", "şah", "şen", "şirin", "şeb", "peri", "dil", "bahar", "cihan", "can", 
      "afitap", "hürrem", "humay", "nevruz", "nergis", "naz", "nazlı", "nigar", 
      "şems", "dilber", "dilara", "delara", "peyker", "perver", "pür", "nev", 
      "nev", "naz", "dilşad", "gülşen"
    ],
    "İbranice": [
      "havva", "adem", "musa", "davut", "meryem", "sara", "mikail", "cebrail", 
      "süleyman", "yakup", "yunus", "yusuf", "zekeria", "zekeriya", "danyal", 
      "azra", "talha", "idris", "ishak", "lut", "nuh", "eyüp", "harun", "yahya"
    ],
    "Yunanca": [
      "elena", "sofia", "petros", "andreas", "defne", "helena", "nikolas", 
      "melis", "iris", "orfeus", "eros", "afrodit", "evdokia", "vasilis", 
      "kiril", "kilikya", "minos", "aleksander", "isidor", "isidora"
    ],
    "Latince": [
      "viktor", "valentin", "julia", "laura", "maksim", "luna", "augustus", 
      "markus", "maximus", "flora", "klara", "silvanus", "aurelius", "felix", 
      "marius", "martinus", "lucius", "lucia", "primus", "prima"
    ]
  };
  
  // Yabancı harfler kontrolü
  if (lowerName.includes('j') || lowerName.includes('w') || lowerName.includes('x')) {
    origin = "Yabancı Kökenli";
  } 
  else {
    // Tipik son eklere göre köken tespiti
    if (lowerName.endsWith('alp') || lowerName.endsWith('can') || lowerName.endsWith('ay') || 
        lowerName.endsWith('han') || lowerName.endsWith('kaya') || lowerName.endsWith('er')) {
      origin = "Türkçe";
    }
    else if (lowerName.endsWith('nur') || lowerName.endsWith('ullah') || 
             lowerName.endsWith('din') || lowerName.endsWith('eddin')) {
      origin = "Arapça";
    }
    else if (lowerName.endsWith('gül') || lowerName.endsWith('şah') || 
             lowerName.endsWith('şen') || lowerName.endsWith('şir')) {
      origin = "Farsça";
    }
    else {
      // Kalıplara göre köken tespiti
      for (const [originName, patterns] of Object.entries(originPatterns)) {
        for (const pattern of patterns) {
          if (lowerName.includes(pattern)) {
            origin = originName;
            break;
          }
        }
        if (origin !== "Bilinmiyor") break;
      }
      
      // İsim yapısına göre ilave kontroller
      if (origin === "Bilinmiyor") {
        // Türkçe isim yapısı: Sesli harfler uyumu
        const vowels = lowerName.match(/[aeıioöuü]/g) || [];
        const frontVowels = vowels.filter(v => "eiöü".includes(v));
        const backVowels = vowels.filter(v => "aıou".includes(v));
        
        // Türkçe'de genellikle bir isimde ya ön ya da arka ünlüler baskındır
        if (frontVowels.length > 0 && backVowels.length === 0) origin = "Türkçe";
        else if (backVowels.length > 0 && frontVowels.length === 0) origin = "Türkçe";
        
        // Arapça ve Farsça isimlerde çok sayıda 'h' harfi veya çift ünsüz
        if ((lowerName.match(/h/g) || []).length >= 2 || 
            lowerName.includes('mm') || lowerName.includes('ll') || 
            lowerName.includes('dd') || lowerName.includes('tt')) {
          origin = "Arapça/Farsça";
        }
      }
    }
  }
  
  return {
    meaning: "Bu ismin güzelliği, kendine has bir anlam taşımasıdır. Her harf isime özel bir karakter katar.",
    origin: origin,
    gender: gender,
    letterMeanings: letterAnalysis,
    numerology: numerologySum,
    numerologyMeaning: numerologyMeanings[numerologySum] || "Bu sayı için anlam bulunamadı.",
    description: nameDescription
  };
};
