import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
// import angelImage from '../assets/angel.png'; // Eğer melek resmi assets klasöründeyse bu şekilde import edin

const symbols = ['Σ', 'π', '∞', '∆', 'Ω', '√'];

function IntroAnimation() {
  const [showSymbols, setShowSymbols] = useState(true);
  // Statik URL yolunu kullanıyoruz, dosyayı public klasörüne koymalısınız
  // const [audio] = useState(new Audio('/assets/mystical-background.mp3'));

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowSymbols(false);
    }, 4000);

    return () => clearTimeout(timer);
  }, []);

  // Müzik için önce Audio öğesinin yüklenip yüklenmediğini kontrol edelim
  // useEffect(() => {
  //   // Müzik ayarları
  //   if (audio) {
  //     audio.volume = 0.2; // Ses seviyesi
  //     audio.loop = true; // Sürekli çalma
      
  //     // Kullanıcı etkileşimi sonrası müziği başlat
  //     const startMusic = () => {
  //       audio.play().catch(error => console.log('Müzik başlatılamadı:', error));
  //       document.removeEventListener('click', startMusic);
  //     };

  //     document.addEventListener('click', startMusic);

  //     return () => {
  //       audio.pause();
  //       audio.currentTime = 0;
  //       document.removeEventListener('click', startMusic);
  //     };
  //   }
  // }, [audio]);

  return (
    <AnimatePresence>
      {showSymbols && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 flex items-center justify-center bg-midnight-default z-50"
        >
          <div className="relative w-64 h-64 flex items-center justify-center">
            {/* Melek görseli */}
            <motion.div
              initial={{ opacity: 0, scale: 0.5 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 2, ease: "easeOut" }}
              className="absolute"
            >
              <img 
                src="/assets/angel.png" 
                alt="Melek" 
                className="w-48 h-48 object-contain"
              />
            </motion.div>

            {/* Merkez sembol */}
            <motion.div
              initial={{ scale: 0, rotate: 0 }}
              animate={{ scale: 1, rotate: 360 }}
              transition={{ duration: 2, ease: "easeOut" }}
              className="absolute"
            >
              <span className="text-6xl font-cinzel text-gold-default">∞</span>
            </motion.div>

            {/* Dönen semboller */}
            {symbols.map((symbol, index) => (
              <motion.div
                key={symbol}
                initial={{ scale: 0, rotate: 0 }}
                animate={{ 
                  scale: 1,
                  rotate: 360,
                  x: Math.cos(index * (2 * Math.PI / symbols.length)) * 80,
                  y: Math.sin(index * (2 * Math.PI / symbols.length)) * 80
                }}
                transition={{ 
                  duration: 2,
                  delay: index * 0.1,
                  ease: "easeOut"
                }}
                className="absolute"
              >
                <span className="text-4xl font-cinzel text-gold-light">{symbol}</span>
              </motion.div>
            ))}

            {/* Radyal gradient efekti */}
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1 }}
              className="absolute inset-0 bg-gradient-to-r from-gold-default/20 via-transparent to-gold-default/20 rounded-full blur-xl"
            />

            {/* Başlık ve Hoşgeldiniz mesajı */}
            <motion.div 
              className="absolute -bottom-28 left-1/2 transform -translate-x-1/2 flex flex-col items-center"
            >
              <motion.h1
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 1, duration: 1 }}
                className="text-3xl font-cinzel text-gold-default whitespace-nowrap"
              >
                Nomen Est Omen
              </motion.h1>
              
              <motion.p
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 1.5, duration: 1 }}
                className="mt-4 text-xl font-cinzel text-gold-light"
              >
                Hoşgeldiniz
              </motion.p>
            </motion.div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

export default IntroAnimation;
