import React, { useState, useEffect } from 'react';
import { MagnifyingGlassIcon, SpeakerWaveIcon, SpeakerXMarkIcon, ShareIcon, ChartBarIcon, SparklesIcon, LanguageIcon } from '@heroicons/react/24/outline';
import { nameCharacteristics } from './data/nameCharacteristics';
import IntroAnimation from './components/IntroAnimation';
import NameSuggestions from './components/NameSuggestions';
import { motion } from 'framer-motion';
import WelcomeScreen from './components/WelcomeScreen';
import AdvancedAnalysis from './components/AdvancedAnalysis';
import { StarIcon } from '@heroicons/react/24/outline';

function App() {
  const [name, setName] = useState('');
  const [result, setResult] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [showStats, setShowStats] = useState(false);
  const [showCombinations, setShowCombinations] = useState(false);
  const [showIntro, setShowIntro] = useState(true);
  const [language, setLanguage] = useState('tr');
  const [audio] = useState(new Audio('/assets/mystical-background.mp3'));
  const [hoverSound] = useState(new Audio('/assets/hover-chime.mp3'));
  const [clickSound] = useState(new Audio('/assets/click-effect.mp3'));
  const [showWelcome, setShowWelcome] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    audio.loop = true;
  }, [audio]);

  const playHoverSound = () => {
    hoverSound.currentTime = 0;
    hoverSound.play().catch(() => {});
  };

  const playClickSound = () => {
    clickSound.currentTime = 0;
    clickSound.play().catch(() => {});
  };

  const togglePlay = () => {
    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
    playClickSound();
  };

  const handleSearch = () => {
    if (name.trim()) {
      playClickSound();
      const firstLetter = name.charAt(0).toUpperCase();
      setResult(nameCharacteristics[firstLetter] || {
        description: language === 'tr' 
          ? 'Bu isim için henüz bir karakter analizi bulunmamaktadır.'
          : 'Character analysis for this name is not available yet.',
        traits: [],
        origin: language === 'tr' 
          ? 'Köken bilgisi mevcut değil.'
          : 'Origin information not available.'
      });
      setShowStats(true);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const handleShare = async () => {
    const shareText = `${name} isminin analizi:\n${result}\n\nSen de isminin sırlarını keşfet!`;
    
    if (navigator.share) {
      try {
        await navigator.share({
          title: 'İsim Analizi',
          text: shareText,
          url: window.location.href
        });
      } catch (err) {
        navigator.clipboard.writeText(shareText);
        alert('Sonuçlar panoya kopyalandı!');
      }
    } else {
      navigator.clipboard.writeText(shareText);
      alert('Sonuçlar panoya kopyalandı!');
    }
  };

  const getNameStatistics = () => {
    // Örnek istatistikler (gerçek verilerle değiştirilmeli)
    return {
      popularity: Math.floor(Math.random() * 100),
      commonAge: Math.floor(Math.random() * 50) + 20,
      similarNames: Object.keys(nameCharacteristics)
        .filter(letter => letter === name.charAt(0).toUpperCase())
        .map(() => {
          const randomNames = [
            'Ali', 'Ahmet', 'Ayşe', 'Berna', 'Burak', 'Ceyda', 
            'Deniz', 'Ece', 'Fatma', 'Gökhan', 'Hande', 'İrem'
          ];
          return randomNames[Math.floor(Math.random() * randomNames.length)];
        })
        .slice(0, 3)
    };
  };

  const getNameCombinations = () => {
    const firstLetter = name.charAt(0).toUpperCase();
    const traits = nameCharacteristics[firstLetter]?.traits || [];
    
    return {
      bestMatches: Object.entries(nameCharacteristics)
        .filter(([letter]) => letter !== firstLetter)
        .map(([letter, data]) => ({
          letter,
          compatibility: data.traits.filter(t => traits.includes(t)).length,
          traits: data.traits
        }))
        .sort((a, b) => b.compatibility - a.compatibility)
        .slice(0, 3)
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name) return;

    setIsLoading(true);
    playClickSound();

    // Yapay yükleme gecikmesi
    setTimeout(() => {
      setResult(true);
      setIsLoading(false);
    }, 1500);
  };

  if (showIntro) {
    return <IntroAnimation onComplete={() => setShowIntro(false)} />;
  }

  return (
    <div className="min-h-screen bg-[#0A0B1A] text-white overflow-x-hidden">
      {showWelcome && (
        <WelcomeScreen onComplete={() => setShowWelcome(false)} />
      )}
      
      <div className={`transition-opacity duration-1000 ${showWelcome ? 'opacity-0' : 'opacity-100'}`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Header */}
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className="text-center mb-12"
          >
            <h1 className="text-3xl sm:text-4xl md:text-5xl font-serif font-bold bg-gradient-to-r from-[#FFB800] to-[#FFD700] bg-clip-text text-transparent mb-4">
              İsim Analizi
            </h1>
            <p className="text-[#FFB800]/80 text-sm sm:text-base md:text-lg max-w-2xl mx-auto">
              İsminizdeki gizli anlamları ve karakteristik özellikleri keşfedin
            </p>
          </motion.div>

          {/* Ana Form */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="max-w-md mx-auto mb-12"
          >
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="relative">
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder={language === 'tr' ? 'İsminizi girin...' : 'Enter your name...'}
                  className="w-full px-6 py-4 bg-[#141832]/40 rounded-lg border border-[#FFB800]/20 focus:border-[#FFB800] focus:ring-2 focus:ring-[#FFB800]/20 transition-all text-lg placeholder-[#FFB800]/40 backdrop-blur-xl"
                  onMouseEnter={playHoverSound}
                />
                <SparklesIcon className="absolute right-4 top-1/2 transform -translate-y-1/2 h-5 w-5 text-[#FFB800]/40" />
              </div>

              <button
                type="submit"
                disabled={!name || isLoading}
                className="w-full px-6 py-4 bg-gradient-to-r from-[#FFB800] to-[#FFD700] rounded-lg text-[#0A0B1A] font-semibold text-lg hover:opacity-90 transition-all disabled:opacity-50 disabled:cursor-not-allowed backdrop-blur-xl"
                onMouseEnter={playHoverSound}
              >
                {isLoading ? (
                  <span className="flex items-center justify-center">
                    <StarIcon className="animate-spin h-5 w-5 mr-2" />
                    {language === 'tr' ? 'Analiz ediliyor...' : 'Analyzing...'}
                  </span>
                ) : (
                  language === 'tr' ? 'Analiz Et' : 'Analyze'
                )}
              </button>
            </form>
          </motion.div>

          {/* Sonuçlar */}
          {result && (
            <motion.div
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              className="space-y-8"
            >
              <AdvancedAnalysis name={name} language={language} />

              {/* Paylaş Butonu */}
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.5 }}
                className="flex justify-center"
              >
                <button
                  onClick={handleShare}
                  className="flex items-center gap-2 px-6 py-3 bg-[#141832]/40 rounded-lg border border-[#FFB800]/20 text-[#FFB800] hover:bg-[#FFB800]/10 transition-all"
                  onMouseEnter={playHoverSound}
                >
                  <ShareIcon className="h-5 w-5" />
                  {language === 'tr' ? 'Sonuçları Paylaş' : 'Share Results'}
                </button>
              </motion.div>
            </motion.div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
