import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const symbols = ['Σ', 'π', '∞', '∆', 'Ω', '√'];

const IntroAnimation = () => {
  const [showSymbols, setShowSymbols] = useState(true);
  const [showWelcome, setShowWelcome] = useState(false);
  const magicalSound = new Audio('/assets/magical-chime.mp3');

  useEffect(() => {
    const timer = setTimeout(() => {
      magicalSound.play();
      setShowSymbols(false);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowWelcome(true);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-midnight z-50">
      <AnimatePresence>
        {showSymbols && (
          <motion.div
            initial={{ opacity: 0, scale: 0.5 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 1.5 }}
            className="relative"
          >
            <motion.div
              className="w-64 h-64 rounded-full overflow-hidden"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5 }}
            >
              <img
                src="/assets/angel.jpg"
                alt="Angel"
                className="w-full h-full object-cover"
              />
            </motion.div>
            <motion.div
              className="absolute inset-0 flex items-center justify-center"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 1 }}
            >
              <div className="text-center">
                <motion.h1
                  className="text-4xl font-cinzel text-gold mb-4"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 1.5 }}
                >
                  Hoş Geldiniz
                </motion.h1>
                <motion.p
                  className="text-gold-light"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 2 }}
                >
                  İsminizin sırlarını keşfedin
                </motion.p>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default IntroAnimation;
