import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MagnifyingGlassIcon, 
  ShareIcon, 
  LanguageIcon,
  StarIcon 
} from '@heroicons/react/24/outline';
import IntroAnimation from './components/IntroAnimation';
import NameSuggestions from './components/NameSuggestions';
import './App.css';
import { nameCharacteristics } from './data/nameCharacteristics';
import WelcomeScreen from './components/WelcomeScreen';
import AdvancedAnalysis from './components/AdvancedAnalysis';

function App() {
  const [name, setName] = useState('');
  const [result, setResult] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [showIntro, setShowIntro] = useState(true);
  const [language, setLanguage] = useState('tr');
  const [isLoading, setIsLoading] = useState(false);
  const [isSoundEnabled, setIsSoundEnabled] = useState(false);
  const [isHovering, setIsHovering] = useState(false);
  const [showNameSuggestions, setShowNameSuggestions] = useState(false);
  
  const hoverSound = React.useRef(null);
  const clickSound = React.useRef(null);
  const backgroundMusic = React.useRef(null);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowIntro(false);
    }, 4000);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    hoverSound.current = new Audio('/assets/hover.mp3');
    clickSound.current = new Audio('/assets/click.mp3');
    backgroundMusic.current = new Audio('/assets/background-music.mp3');
    backgroundMusic.current.loop = true;
    backgroundMusic.current.volume = 0.3;

    const handleUserInteraction = () => {
      setIsSoundEnabled(true);
    };

    document.addEventListener('click', handleUserInteraction);
    document.addEventListener('keydown', handleUserInteraction);
    document.addEventListener('touchstart', handleUserInteraction);

    return () => {
      document.removeEventListener('click', handleUserInteraction);
      document.removeEventListener('keydown', handleUserInteraction);
      document.removeEventListener('touchstart', handleUserInteraction);
    };
  }, []);

  const playHoverSound = () => {
    if (isSoundEnabled && hoverSound.current) {
      hoverSound.current.currentTime = 0;
      hoverSound.current.play().catch(() => {});
    }
  };

  const playClickSound = () => {
    if (isSoundEnabled && clickSound.current) {
      clickSound.current.currentTime = 0;
      clickSound.current.play().catch(() => {});
    }
  };

  const togglePlay = () => {
    if (isPlaying) {
      backgroundMusic.current.pause();
    } else {
      backgroundMusic.current.play().catch(() => {});
    }
    setIsPlaying(!isPlaying);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (!name) return;

    setIsLoading(true);
    playClickSound();

    setTimeout(() => {
      const firstLetter = name.charAt(0).toUpperCase();
      const analysis = nameCharacteristics[firstLetter] || {
        description: language === 'tr' 
          ? 'Bu isim için henüz bir karakter analizi bulunmamaktadır.'
          : 'Character analysis for this name is not available yet.',
        traits: [],
        origin: language === 'tr' 
          ? 'Köken bilgisi mevcut değil.'
          : 'Origin information not available.'
      };

      setResult(analysis);
      setIsLoading(false);
    }, 1500);
  };

  const handleShare = async () => {
    playClickSound();
    const shareText = `${name} isminin analizi:\n${result}\n\nSen de isminin sırlarını keşfet!`;
    
    if (navigator.share) {
      try {
        await navigator.share({
          title: 'İsim Analizi',
          text: shareText,
          url: window.location.href
        });
      } catch (err) {
        navigator.clipboard.writeText(shareText);
        alert(language === 'tr' ? 'Sonuçlar panoya kopyalandı!' : 'Results copied to clipboard!');
      }
    } else {
      navigator.clipboard.writeText(shareText);
      alert(language === 'tr' ? 'Sonuçlar panoya kopyalandı!' : 'Results copied to clipboard!');
    }
  };

  const handleHover = () => {
    if (!isHovering) {
      playHoverSound();
      setIsHovering(true);
    }
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-midnight to-midnight-light text-white relative overflow-hidden">
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,_rgba(255,184,0,0.1)_0%,_transparent_70%)]" />
      
      <AnimatePresence>
        {showIntro && <IntroAnimation />}
      </AnimatePresence>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 3.5 }}
        className="container mx-auto px-4 py-8"
      >
        <header className="flex justify-between items-center mb-12">
          <h1 className="text-4xl md:text-6xl font-cinzel bg-gradient-to-r from-gold to-gold-light bg-clip-text text-transparent animate-glow">
            {language === 'tr' ? 'İsim Analizi' : 'Name Analysis'}
          </h1>
          <div className="flex items-center gap-4">
            <button
              onClick={togglePlay}
              className="p-2 rounded-full hover:bg-gold/20 transition-colors"
              onMouseEnter={handleHover}
              onMouseLeave={handleMouseLeave}
            >
              {isPlaying ? (
                <span className="text-gold text-xl">♪</span>
              ) : (
                <span className="text-gold-light text-xl">♪</span>
              )}
            </button>
            <button
              onClick={() => setLanguage(lang => lang === 'tr' ? 'en' : 'tr')}
              className="p-2 rounded-full hover:bg-gold/20 transition-colors"
              onMouseEnter={handleHover}
              onMouseLeave={handleMouseLeave}
            >
              <LanguageIcon className="w-6 h-6 text-gold" />
            </button>
          </div>
        </header>

        <div className="max-w-2xl mx-auto">
          <form onSubmit={handleSearch} className="mb-8">
            <div className="relative">
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder={language === 'tr' ? 'İsminizi girin...' : 'Enter your name...'}
                className="w-full px-6 py-4 bg-midnight-light/30 rounded-lg border-2 border-gold/30 focus:border-gold outline-none text-lg placeholder-gold/50 backdrop-blur-sm"
                onMouseEnter={handleHover}
                onMouseLeave={handleMouseLeave}
              />
              <button
                type="submit"
                className="absolute right-2 top-1/2 -translate-y-1/2 p-2 rounded-full hover:bg-gold/20 transition-colors"
                onMouseEnter={handleHover}
                onMouseLeave={handleMouseLeave}
              >
                <MagnifyingGlassIcon className="w-6 h-6 text-gold" />
              </button>
            </div>
          </form>

          {isLoading && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-8"
            >
              <div className="inline-block animate-spin text-gold text-4xl">✧</div>
            </motion.div>
          )}

          {result && !isLoading && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-midnight-light/30 backdrop-blur-sm rounded-lg p-6 border-2 border-gold/30 hover:border-gold/50 transition-colors"
            >
              <div className="flex justify-between items-start mb-4">
                <h2 className="text-2xl font-cinzel text-gold animate-glow">
                  {name}
                </h2>
                <button
                  onClick={handleShare}
                  className="p-2 rounded-full hover:bg-gold/20 transition-colors"
                  onMouseEnter={handleHover}
                  onMouseLeave={handleMouseLeave}
                >
                  <ShareIcon className="w-5 h-5 text-gold" />
                </button>
              </div>
              <p className="mb-4 text-lg leading-relaxed text-gold-light">
                {result.description}
              </p>
              {result.traits.length > 0 && (
                <div className="flex flex-wrap gap-2">
                  {result.traits.map((trait, index) => (
                    <span
                      key={index}
                      className="px-3 py-1 rounded-full bg-gold/20 text-gold text-sm hover:bg-gold/30 transition-colors cursor-default"
                    >
                      {trait}
                    </span>
                  ))}
                </div>
              )}
            </motion.div>
          )}
        </div>

        {showNameSuggestions && (
          <NameSuggestions language={language} />
        )}
      </motion.div>
    </div>
  );
}

export default App;
