import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const HoroscopePage = ({ onClose, onExit, language, theme }) => {
  const [selectedZodiac, setSelectedZodiac] = useState('');
  const [horoscopeData, setHoroscopeData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Tema renkleri
  const currentTheme = theme || {
    background: 'bg-midnight-default/80',
    card: 'bg-midnight-light/30',
    section: 'bg-midnight-default/30',
    input: 'bg-midnight-light/20',
    text: 'text-gold-light',
    title: 'text-gold-default',
    border: 'border-gold-default/50',
    borderLight: 'border-gold-default/20',
    accent: 'bg-gold-default/30',
    accentHover: 'hover:bg-gold-default/40',
    borderAccent: 'border-gold-default/40'
  };

  // Burç listesi
  const zodiacs = {
    tr: [
      { id: 'koc', name: 'Koç' },
      { id: 'boga', name: 'Boğa' },
      { id: 'ikizler', name: 'İkizler' },
      { id: 'yengec', name: 'Yengeç' },
      { id: 'aslan', name: 'Aslan' },
      { id: 'basak', name: 'Başak' },
      { id: 'terazi', name: 'Terazi' },
      { id: 'akrep', name: 'Akrep' },
      { id: 'yay', name: 'Yay' },
      { id: 'oglak', name: 'Oğlak' },
      { id: 'kova', name: 'Kova' },
      { id: 'balik', name: 'Balık' }
    ],
    en: [
      { id: 'aries', name: 'Aries' },
      { id: 'taurus', name: 'Taurus' },
      { id: 'gemini', name: 'Gemini' },
      { id: 'cancer', name: 'Cancer' },
      { id: 'leo', name: 'Leo' },
      { id: 'virgo', name: 'Virgo' },
      { id: 'libra', name: 'Libra' },
      { id: 'scorpio', name: 'Scorpio' },
      { id: 'sagittarius', name: 'Sagittarius' },
      { id: 'capricorn', name: 'Capricorn' },
      { id: 'aquarius', name: 'Aquarius' },
      { id: 'pisces', name: 'Pisces' }
    ]
  };

  // Çeviriler
  const translations = {
    tr: {
      title: "Günlük Burç Yorumu",
      selectZodiac: "Burcunuzu Seçiniz",
      loading: "Burç yorumu yükleniyor...",
      error: "Hata oluştu:",
      dailyHoroscope: "Günlük Burç Yorumu",
      date: "Bugünün Tarihi:",
      close: "Kapat",
      general: "Genel",
      love: "Aşk",
      career: "Kariyer",
      health: "Sağlık",
      dailyUpdate: "Yorumlar her gün güncellenir",
      returnToApp: "Uygulamaya Dön",
      exit: "Çıkış Yap"
    },
    en: {
      title: "Daily Horoscope",
      selectZodiac: "Select Your Zodiac Sign",
      loading: "Loading horoscope...",
      error: "An error occurred:",
      dailyHoroscope: "Daily Horoscope",
      date: "Today's Date:",
      close: "Close",
      general: "General",
      love: "Love",
      career: "Career",
      health: "Health",
      dailyUpdate: "Horoscopes updated daily",
      returnToApp: "Return to App",
      exit: "Exit"
    }
  };

  // Burç yorumunu simüle et
  const fetchHoroscope = (sign) => {
    if (!sign) return;
    
    setLoading(true);
    setError(null);

    // Simülasyon için gecikme
    setTimeout(() => {
      try {
        setHoroscopeData({
          sign: zodiacs[language].find(z => z.id === sign).name,
          date: new Date().toLocaleDateString(language === 'tr' ? 'tr-TR' : 'en-US', { 
            weekday: 'long', 
            year: 'numeric', 
            month: 'long', 
            day: 'numeric' 
          }),
          text: {
            general: language === 'tr' 
              ? "Bugün enerjiniz yüksek olacak ve yeni fırsatlara açık olacaksınız."
              : "Today your energy will be high and you will be open to new opportunities.",
            love: language === 'tr'
              ? "İlişkilerinizde açık iletişim kurmanız önemli olacak."
              : "Open communication in your relationships will be important.",
            career: language === 'tr'
              ? "İş hayatınızda yeni projeler başlatmak için uygun bir gün."
              : "It's a good day to start new projects in your career.",
            health: language === 'tr'
              ? "Fiziksel aktivitelere zaman ayırın, enerjinizi dengeleyecektir."
              : "Make time for physical activities, it will balance your energy."
          }
        });
        setLoading(false);
      } catch (err) {
        setError(language === 'tr' ? 'Burç yorumu alınırken bir hata oluştu.' : 'An error occurred while getting the horoscope.');
        setLoading(false);
      }
    }, 1000);
  };

  // Uygulamaya dön fonksiyonu
  const handleReturnToApp = () => {
    if (typeof onClose === 'function') {
      onClose();
    }
  };

  // Çıkış yap fonksiyonu
  const handleExit = () => {
    if (typeof onExit === 'function') {
      onExit();
    } else {
      // Fallback - Sayfayı yenileme
      window.location.reload();
    }
  };

  // Seçilen burç değiştiğinde yorumu getir
  useEffect(() => {
    if (selectedZodiac) {
      fetchHoroscope(selectedZodiac);
    }
  }, [selectedZodiac, language]);

  const t = translations[language];

  return (
    <div className={`fixed inset-0 ${currentTheme.background} z-50 overflow-y-auto`}>
      <div className="min-h-screen py-8 px-4">
        <div className="max-w-4xl mx-auto">
          {/* Başlık ve geri dönüş butonu */}
          <div className="flex justify-between items-center mb-8">
            <h1 className={`text-3xl md:text-4xl font-cinzel ${currentTheme.title}`}>
              {t.title}
            </h1>
            <button 
              onClick={handleReturnToApp}
              className={`${currentTheme.text} hover:${currentTheme.accentText} transition-colors`}
              aria-label="Geri Dön"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          {/* Burç seçimi ve sonuç kartları */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {/* Burç seçim paneli */}
            <div className={`${currentTheme.card} ${currentTheme.border} border rounded-2xl p-6 shadow-lg backdrop-blur-sm md:col-span-1`}>
              <h2 className={`text-xl font-cinzel ${currentTheme.text} mb-4`}>{t.selectZodiac}</h2>
              
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-1 gap-2">
                {zodiacs[language].map((zodiac) => (
                  <button
                    key={zodiac.id}
                    onClick={() => setSelectedZodiac(zodiac.id)}
                    className={`p-3 rounded-lg transition-all ${
                      selectedZodiac === zodiac.id
                        ? `${currentTheme.section} ${currentTheme.accentText} font-bold ${currentTheme.border}`
                        : `${currentTheme.input} ${currentTheme.text} ${currentTheme.borderLight} hover:${currentTheme.border}`
                    } border text-sm`}
                  >
                    {zodiac.name}
                  </button>
                ))}
              </div>
            </div>

            {/* Burç yorum alanı */}
            <div className={`${currentTheme.card} ${currentTheme.border} border rounded-2xl p-6 shadow-lg backdrop-blur-sm md:col-span-2`}>
              {loading ? (
                <div className="flex items-center justify-center p-8">
                  <svg className="animate-spin h-10 w-10 text-gold-default" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                </div>
              ) : error ? (
                <div className={`${currentTheme.text} p-6 rounded-lg text-center`}>
                  <p className="text-red-400">{error}</p>
                </div>
              ) : selectedZodiac ? (
                horoscopeData ? (
                  <div>
                    <div className="mb-6">
                      <div className={`${currentTheme.text} text-sm`}>{t.date} {horoscopeData.date}</div>
                      <h3 className={`text-2xl font-cinzel ${currentTheme.accentText} mt-2`}>{horoscopeData.sign}</h3>
                    </div>
                    
                    <div className="space-y-6">
                      <div>
                        <h4 className={`${currentTheme.accentText} font-cinzel text-lg mb-2`}>{t.general}</h4>
                        <p className={`${currentTheme.text}`}>{horoscopeData.text.general}</p>
                      </div>
                      
                      <div>
                        <h4 className={`${currentTheme.accentText} font-cinzel text-lg mb-2`}>{t.love}</h4>
                        <p className={`${currentTheme.text}`}>{horoscopeData.text.love}</p>
                      </div>
                      
                      <div>
                        <h4 className={`${currentTheme.accentText} font-cinzel text-lg mb-2`}>{t.career}</h4>
                        <p className={`${currentTheme.text}`}>{horoscopeData.text.career}</p>
                      </div>
                      
                      <div>
                        <h4 className={`${currentTheme.accentText} font-cinzel text-lg mb-2`}>{t.health}</h4>
                        <p className={`${currentTheme.text}`}>{horoscopeData.text.health}</p>
                      </div>
                    </div>

                    <div className="text-xs text-center mt-8 text-gold-light/70">
                      {t.dailyUpdate}
                    </div>
                  </div>
                ) : (
                  <div className="flex flex-col items-center justify-center h-full p-8">
                    <p className={`${currentTheme.text} text-center`}>
                      {language === 'tr' ? 'Lütfen bekleyin...' : 'Please wait...'}
                    </p>
                  </div>
                )
              ) : (
                <div className="flex flex-col items-center justify-center h-full p-8">
                  <p className={`${currentTheme.text} text-center`}>
                    {language === 'tr' ? 'Burcunuzu seçerek günlük yorumu görüntüleyebilirsiniz' : 'Select your zodiac sign to view your daily horoscope'}
                  </p>
                </div>
              )}
            </div>
          </div>

          {/* Kontrol butonları */}
          <div className="flex justify-between mt-10">
            <motion.button
              onClick={handleReturnToApp}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`px-6 py-3 rounded-lg ${currentTheme.input} ${currentTheme.text} ${currentTheme.border} border backdrop-blur-sm transition-colors duration-300`}
            >
              {t.returnToApp}
            </motion.button>
            
            <motion.button
              onClick={handleExit}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`px-6 py-3 rounded-lg ${currentTheme.section} ${currentTheme.accentText} ${currentTheme.border} border backdrop-blur-sm transition-colors duration-300`}
            >
              {t.exit}
            </motion.button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HoroscopePage;
