import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MagnifyingGlassIcon, 
  ShareIcon, 
  LanguageIcon,
  ChartBarIcon,
  SparklesIcon
} from '@heroicons/react/24/outline';
import IntroAnimation from './components/IntroAnimation';
import NameSuggestions from './components/NameSuggestions';
import './App.css';
import { nameCharacteristics } from './data/nameCharacteristics';
import WelcomeScreen from './components/WelcomeScreen';
import AdvancedAnalysis from './components/AdvancedAnalysis';

function App() {
  const [showIntro, setShowIntro] = useState(true);
  const [language, setLanguage] = useState('tr');
  const [searchTerm, setSearchTerm] = useState('');
  const [result, setResult] = useState(null);
  const [hoverSound] = useState(new Audio('/assets/hover.mp3'));
  const [clickSound] = useState(new Audio('/assets/click.mp3'));

  const translations = {
    tr: {
      title: 'Nomen Est Omen',
      subtitle: 'İsminizin Sırrını Keşfedin',
      searchPlaceholder: 'İsminizi girin...',
      analyze: 'Analiz Et',
      share: 'Paylaş',
      language: 'Dil',
      characteristics: 'Karakter Özellikleri',
      energy: 'Enerji Seviyesi',
      compatibility: 'Uyumluluk',
      suggestions: 'İsim Önerileri'
    },
    en: {
      title: 'Nomen Est Omen',
      subtitle: 'Discover the Secret of Your Name',
      searchPlaceholder: 'Enter your name...',
      analyze: 'Analyze',
      share: 'Share',
      language: 'Language',
      characteristics: 'Character Traits',
      energy: 'Energy Level',
      compatibility: 'Compatibility',
      suggestions: 'Name Suggestions'
    }
  };

  useEffect(() => {
    setTimeout(() => {
      setShowIntro(false);
    }, 4000);
  }, []);

  const handleSearch = () => {
    if (!searchTerm.trim()) return;
    
    clickSound.play();
    const firstLetter = searchTerm[0].toLowerCase();
    
    if (nameCharacteristics[firstLetter]) {
      setResult({
        name: searchTerm,
        characteristics: nameCharacteristics[firstLetter],
        energy: Math.floor(Math.random() * 41) + 60 // 60-100 arası rastgele enerji
      });
    } else {
      setResult({
        name: searchTerm,
        message: language === 'tr' ? 'Bu isim veritabanımızda bulunmamaktadır.' : 'This name is not in our database.'
      });
    }
  };

  const handleShare = async () => {
    const shareText = language === 'tr' 
      ? `${result.name} isminin analizi:\n${result.characteristics.description}\n\nKarakteristik özellikler: ${result.characteristics.traits.join(', ')}`
      : `Analysis of the name ${result.name}:\n${result.characteristics.description}\n\nCharacteristic traits: ${result.characteristics.traits.join(', ')}`;

    if (navigator.share) {
      try {
        await navigator.share({
          title: 'İsim Analizi',
          text: shareText
        });
      } catch (err) {
        console.log('Paylaşım iptal edildi');
      }
    } else {
      navigator.clipboard.writeText(shareText);
      alert(language === 'tr' ? 'Sonuçlar panoya kopyalandı!' : 'Results copied to clipboard!');
    }
  };

  const toggleLanguage = () => {
    setLanguage(prev => prev === 'tr' ? 'en' : 'tr');
    clickSound.play();
  };

  return (
    <div className="min-h-screen bg-[#0A0B1A] text-gold-light relative overflow-hidden">
      <AnimatePresence>
        {showIntro && <IntroAnimation />}
      </AnimatePresence>

      <div className="absolute inset-0 bg-[url('./assets/stars.png')] opacity-20"></div>
      
      <div className="container mx-auto px-4 py-8 relative z-10">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-center mb-12"
        >
          <h1 className="text-5xl font-cinzel font-bold bg-gradient-to-r from-gold to-gold-light bg-clip-text text-transparent mb-4">
            Nomen Est Omen
          </h1>
          <p className="text-xl text-gold-light/80 font-cormorant">
            {language === 'tr' ? 'İsminizin sırrını keşfedin' : 'Discover the secret of your name'}
          </p>
        </motion.div>

        <div className="max-w-2xl mx-auto">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="bg-midnight/30 backdrop-blur-sm rounded-lg p-6 border border-gold/20"
          >
            <div className="flex gap-4 mb-6">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                placeholder={language === 'tr' ? 'İsminizi girin...' : 'Enter your name...'}
                className="flex-1 bg-midnight-light/20 border border-gold/20 rounded-lg px-4 py-2 text-gold-light focus:outline-none focus:border-gold/40"
              />
              <button
                onClick={handleSearch}
                onMouseEnter={() => hoverSound.play()}
                className="bg-gold/20 hover:bg-gold/30 text-gold-light px-6 py-2 rounded-lg transition-all duration-300"
              >
                {language === 'tr' ? 'Analiz Et' : 'Analyze'}
              </button>
              <button
                onClick={toggleLanguage}
                onMouseEnter={() => hoverSound.play()}
                className="bg-midnight-light/20 hover:bg-midnight-light/30 text-gold-light p-2 rounded-lg transition-all duration-300"
              >
                <LanguageIcon className="w-6 h-6" />
              </button>
            </div>

            <AnimatePresence>
              {result && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  className="mt-8"
                >
                  {result.message ? (
                    <p className="text-gold-light/80 text-center">{result.message}</p>
                  ) : (
                    <div className="space-y-6">
                      <div className="text-center">
                        <h2 className="text-3xl font-cinzel text-gold mb-2">{result.name}</h2>
                        <div className="w-full bg-midnight-light/20 rounded-full h-2.5">
                          <motion.div
                            initial={{ width: 0 }}
                            animate={{ width: `${result.energy}%` }}
                            transition={{ duration: 1, ease: "easeOut" }}
                            className="bg-gold h-2.5 rounded-full"
                          ></motion.div>
                        </div>
                        <p className="text-gold-light/60 mt-2">
                          {language === 'tr' ? 'İsim Enerjisi' : 'Name Energy'}: {result.energy}%
                        </p>
                      </div>

                      <p className="text-gold-light/90 text-center font-cormorant">
                        {result.characteristics.description}
                      </p>

                      <div className="grid grid-cols-2 gap-4">
                        {result.characteristics.traits.map((trait, index) => (
                          <motion.div
                            key={index}
                            initial={{ opacity: 0, x: -20 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ duration: 0.5, delay: index * 0.1 }}
                            className="bg-midnight-light/20 p-3 rounded-lg text-center"
                          >
                            {trait}
                          </motion.div>
                        ))}
                      </div>

                      <div className="flex justify-center">
                        <button
                          onClick={handleShare}
                          onMouseEnter={() => hoverSound.play()}
                          className="flex items-center gap-2 bg-gold/20 hover:bg-gold/30 text-gold-light px-6 py-2 rounded-lg transition-all duration-300"
                        >
                          <ShareIcon className="w-5 h-5" />
                          {language === 'tr' ? 'Paylaş' : 'Share'}
                        </button>
                      </div>
                    </div>
                  )}
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>

          <NameSuggestions language={language} />
        </div>
      </div>
    </div>
  );
}

export default App;
