import React, { useState, useEffect } from 'react';
import { LanguageIcon } from '@heroicons/react/24/outline';
import { nameCharacteristics } from './data/nameCharacteristics';
import IntroAnimation from './components/IntroAnimation';
import NameSuggestions from './components/NameSuggestions';
import { motion, AnimatePresence } from 'framer-motion';
import WelcomeScreen from './components/WelcomeScreen';
import AdvancedAnalysis from './components/AdvancedAnalysis';
import { StarIcon } from '@heroicons/react/24/outline';

function App() {
  const [name, setName] = useState('');
  const [result, setResult] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [showStats, setShowStats] = useState(false);
  const [showCombinations, setShowCombinations] = useState(false);
  const [showIntro, setShowIntro] = useState(true);
  const [language, setLanguage] = useState('tr');
  const [audio] = useState(new Audio('/assets/mystical-background.mp3'));
  const [hoverSound] = useState(new Audio('/assets/hover-chime.mp3'));
  const [clickSound] = useState(new Audio('/assets/click-effect.mp3'));
  const [showWelcome, setShowWelcome] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [soundEnabled, setSoundEnabled] = useState(false);
  const [isHovering, setIsHovering] = useState(false);
  const [isSoundEnabled, setIsSoundEnabled] = useState(false);
  const [backgroundMusic] = useState(new Audio('/assets/background-music.mp3'));

  useEffect(() => {
    audio.loop = true;
    const enableSound = () => {
      setSoundEnabled(true);
      // Kullanıcı etkileşimi olduğunda bir kere ses çalarak initialize edelim
      const initSound = new Audio('/assets/hover-chime.mp3');
      initSound.volume = 0.1;
      initSound.play().catch(() => {});
      // Event listener'ı kaldıralım
      document.removeEventListener('click', enableSound);
    };

    document.addEventListener('click', enableSound);
    return () => document.removeEventListener('click', enableSound);
  }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowIntro(false);
    }, 4000);
    return () => clearTimeout(timer);
  }, []);

  const playHoverSound = () => {
    if (isSoundEnabled && hoverSound.current) {
      hoverSound.current.currentTime = 0;
      hoverSound.current.play().catch(error => {
        console.log('Ses çalma hatası:', error);
      });
    }
  };

  const playClickSound = () => {
    if (isSoundEnabled && clickSound.current) {
      clickSound.current.currentTime = 0;
      clickSound.current.play().catch(error => {
        console.log('Ses çalma hatası:', error);
      });
    }
  };

  const togglePlay = () => {
    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
    playClickSound();
  };

  const handleSearch = (e) => {
    e.preventDefault();
    clickSound.play();

    // İsim analizi mantığı burada
    const analysis = {
      traits: language === 'tr' ? 
        ['Liderlik', 'Yaratıcılık', 'Güçlü sezgiler'] :
        ['Leadership', 'Creativity', 'Strong intuition'],
      energy: Math.floor(Math.random() * 30) + 70,
      compatibleLetters: ['M', 'E', 'R']
    };

    setResult(analysis);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch(e);
    }
  };

  const handleShare = async () => {
    playClickSound();
    const shareText = `${name} isminin analizi:\n${result}\n\nSen de isminin sırlarını keşfet!`;
    
    if (navigator.share) {
      try {
        await navigator.share({
          title: 'İsim Analizi',
          text: shareText,
          url: window.location.href
        });
      } catch (err) {
        navigator.clipboard.writeText(shareText);
        alert('Sonuçlar panoya kopyalandı!');
      }
    } else {
      navigator.clipboard.writeText(shareText);
      alert('Sonuçlar panoya kopyalandı!');
    }
  };

  const getNameStatistics = () => {
    // Örnek istatistikler (gerçek verilerle değiştirilmeli)
    return {
      popularity: Math.floor(Math.random() * 100),
      commonAge: Math.floor(Math.random() * 50) + 20,
      similarNames: Object.keys(nameCharacteristics)
        .filter(letter => letter === name.charAt(0).toUpperCase())
        .map(() => {
          const randomNames = [
            'Ali', 'Ahmet', 'Ayşe', 'Berna', 'Burak', 'Ceyda', 
            'Deniz', 'Ece', 'Fatma', 'Gökhan', 'Hande', 'İrem'
          ];
          return randomNames[Math.floor(Math.random() * randomNames.length)];
        })
        .slice(0, 3)
    };
  };

  const getNameCombinations = () => {
    const firstLetter = name.charAt(0).toUpperCase();
    const traits = nameCharacteristics[firstLetter]?.traits || [];
    
    return {
      bestMatches: Object.entries(nameCharacteristics)
        .filter(([letter]) => letter !== firstLetter)
        .map(([letter, data]) => ({
          letter,
          compatibility: data.traits.filter(t => traits.includes(t)).length,
          traits: data.traits
        }))
        .sort((a, b) => b.compatibility - a.compatibility)
        .slice(0, 3)
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name) return;

    setIsLoading(true);
    playClickSound();

    setTimeout(() => {
      const firstLetter = name.charAt(0).toUpperCase();
      const analysis = nameCharacteristics[firstLetter] || {
        description: language === 'tr' 
          ? 'Bu isim için henüz bir karakter analizi bulunmamaktadır.'
          : 'Character analysis for this name is not available yet.',
        traits: [],
        origin: language === 'tr' 
          ? 'Köken bilgisi mevcut değil.'
          : 'Origin information not available.'
      };

      setResult(analysis);
      setIsLoading(false);
    }, 1500);
  };

  const handleHover = () => {
    if (!isHovering) {
      hoverSound.play();
      setIsHovering(true);
    }
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
  };

  useEffect(() => {
    // Ses dosyalarını yükle
    hoverSound.current = new Audio('/assets/hover.mp3');
    clickSound.current = new Audio('/assets/click.mp3');
    backgroundMusic.current = new Audio('/assets/background-music.mp3');
    backgroundMusic.current.loop = true;
    backgroundMusic.current.volume = 0.3;

    // Sayfa yüklendiğinde ses durumunu kontrol et
    const checkAudioState = () => {
      if (document.documentElement.hasAttribute('data-user-interacted')) {
        setIsSoundEnabled(true);
      }
    };

    // Kullanıcı etkileşimini dinle
    const handleUserInteraction = () => {
      document.documentElement.setAttribute('data-user-interacted', 'true');
      setIsSoundEnabled(true);
    };

    document.addEventListener('click', handleUserInteraction);
    document.addEventListener('keydown', handleUserInteraction);
    document.addEventListener('touchstart', handleUserInteraction);

    checkAudioState();

    return () => {
      document.removeEventListener('click', handleUserInteraction);
      document.removeEventListener('keydown', handleUserInteraction);
      document.removeEventListener('touchstart', handleUserInteraction);
    };
  }, []);

  return (
    <div className="min-h-screen bg-midnight text-white relative overflow-hidden">
      <div className="absolute inset-0 bg-[url('/public/assets/stars.png')] opacity-50" />
      
      <AnimatePresence>
        {showIntro && <IntroAnimation />}
      </AnimatePresence>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 3.5 }}
        className="container mx-auto px-4 py-8"
      >
        <header className="flex justify-between items-center mb-12">
          <h1 className="text-4xl md:text-6xl font-cinzel bg-gradient-to-r from-gold to-gold-light bg-clip-text text-transparent">
            {language === 'tr' ? 'İsim Analizi' : 'Name Analysis'}
          </h1>
          
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => {
              clickSound.play();
              setLanguage(prev => prev === 'tr' ? 'en' : 'tr');
            }}
            onMouseEnter={handleHover}
            onMouseLeave={handleMouseLeave}
            className="p-2 rounded-full bg-midnight-light hover:bg-gold/20 transition-colors"
          >
            <LanguageIcon className="w-6 h-6 text-gold" />
          </motion.button>
        </header>

        <form onSubmit={handleSearch} className="max-w-xl mx-auto">
          <div className="relative">
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder={language === 'tr' ? 'İsminizi girin...' : 'Enter your name...'}
              className="w-full px-6 py-4 bg-midnight-light/50 rounded-lg border-2 border-gold/30 focus:border-gold focus:outline-none backdrop-blur-sm text-lg"
              onMouseEnter={handleHover}
              onMouseLeave={handleMouseLeave}
            />
            <button
              type="submit"
              onMouseEnter={handleHover}
              onMouseLeave={handleMouseLeave}
              className="absolute right-2 top-1/2 -translate-y-1/2 px-6 py-2 bg-gold text-midnight font-bold rounded hover:bg-gold-light transition-colors"
            >
              {language === 'tr' ? 'Analiz Et' : 'Analyze'}
            </button>
          </div>
        </form>

        <AnimatePresence>
          {result && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
              className="mt-12 max-w-xl mx-auto p-6 bg-midnight-light/50 rounded-lg border-2 border-gold/30 backdrop-blur-sm"
            >
              <h2 className="text-2xl font-cinzel text-gold mb-4">
                {language === 'tr' ? 'Analiz Sonucu' : 'Analysis Result'}
              </h2>
              <div className="space-y-4">
                <div>
                  <h3 className="text-gold-light mb-2">
                    {language === 'tr' ? 'Karakter Özellikleri' : 'Character Traits'}
                  </h3>
                  <ul className="list-disc list-inside space-y-1 text-gold/90">
                    {result.traits.map((trait, index) => (
                      <li key={index}>{trait}</li>
                    ))}
                  </ul>
                </div>

                <div>
                  <h3 className="text-gold-light mb-2">
                    {language === 'tr' ? 'Enerji Yüzdesi' : 'Energy Percentage'}
                  </h3>
                  <div className="h-4 bg-midnight rounded-full overflow-hidden">
                    <motion.div
                      initial={{ width: 0 }}
                      animate={{ width: `${result.energy}%` }}
                      className="h-full bg-gradient-to-r from-gold to-gold-light"
                    />
                  </div>
                  <p className="text-right mt-1">{result.energy}%</p>
                </div>

                <div>
                  <h3 className="text-gold-light mb-2">
                    {language === 'tr' ? 'Uyumlu Harfler' : 'Compatible Letters'}
                  </h3>
                  <div className="flex gap-2">
                    {result.compatibleLetters.map((letter, index) => (
                      <motion.span
                        key={index}
                        whileHover={{ scale: 1.1 }}
                        className="text-gold"
                      >
                        {letter}
                      </motion.span>
                    ))}
                  </div>
                  <p className="mb-4">{result.traits}</p>
                </div>

                <div className="flex items-center gap-2 mb-4">
                  <span className="text-gold">{language === 'tr' ? 'Enerji' : 'Energy'}:</span>
                  <div className="flex-1 h-2 bg-midnight-light rounded-full overflow-hidden">
                    <motion.div
                      initial={{ width: 0 }}
                      animate={{ width: `${result.energy}%` }}
                      className="h-full bg-gradient-to-r from-gold to-gold-light"
                    />
                  </div>
                  <span>{result.energy}%</span>
                </div>

                <div>
                  <span className="text-gold">
                    {language === 'tr' ? 'Uyumlu Harfler' : 'Compatible Letters'}:
                  </span>
                  <span className="ml-2">{result.compatibleLetters.join(', ')}</span>
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        <NameSuggestions language={language} />
      </motion.div>
    </div>
  );
}

export default App;
