import React, { useState } from 'react';
import { Tab } from '@headlessui/react';
import { ChartBarIcon, SparklesIcon, BookOpenIcon, StarIcon } from '@heroicons/react/24/outline';

const nameCategories = {
  modern: {
    tr: ['Defne', 'Atlas', 'Ada', 'Çınar', 'Toprak', 'Güneş', 'Rüzgar', 'Deniz', 'Yağmur', 'Derin'],
    en: ['Luna', 'Nova', 'Atlas', 'River', 'Sky', 'Rain', 'Storm', 'Ocean', 'Phoenix', 'Aurora']
  },
  classic: {
    tr: ['Mehmet', 'Ahmet', 'Ayşe', 'Fatma', 'Ali', 'Mustafa', 'Emine', 'Hatice', 'Hüseyin', 'İbrahim'],
    en: ['Alexander', 'Elizabeth', 'William', 'Catherine', 'James', 'Victoria', 'Edward', 'Margaret', 'Henry', 'Mary']
  },
  spiritual: {
    tr: ['Muhammed', 'Ahmed', 'Meryem', 'İsa', 'Yakup', 'Yusuf', 'Sara', 'Havva', 'Adem', 'İbrahim'],
    en: ['Gabriel', 'Michael', 'Sarah', 'Mary', 'David', 'Solomon', 'Eve', 'Adam', 'Noah', 'Abraham']
  }
};

const letterEnergies = {
  A: 85, B: 75, C: 70, D: 80, E: 90, F: 65, G: 78, H: 72, I: 88, 
  İ: 88, J: 70, K: 82, L: 76, M: 85, N: 79, O: 86, Ö: 86, P: 73,
  R: 81, S: 84, Ş: 84, T: 77, U: 83, Ü: 83, V: 74, Y: 89, Z: 80
};

export default function NameSuggestions({ language = 'tr' }) {
  const [selectedCategory, setSelectedCategory] = useState('modern');
  const [hoveredLetter, setHoveredLetter] = useState(null);

  const categories = [
    { id: 'modern', icon: SparklesIcon, label: language === 'tr' ? 'Modern' : 'Modern' },
    { id: 'classic', icon: BookOpenIcon, label: language === 'tr' ? 'Klasik' : 'Classic' },
    { id: 'spiritual', icon: StarIcon, label: language === 'tr' ? 'Manevi' : 'Spiritual' }
  ];

  const calculateNameEnergy = (name) => {
    const letters = name.toUpperCase().split('');
    return letters.map(letter => ({
      letter,
      energy: letterEnergies[letter] || 70
    }));
  };

  return (
    <div className="space-y-8">
      <div className="bg-[#141832]/40 backdrop-blur-xl rounded-lg shadow-lg p-8 border border-[#FFB800]/20">
        <h3 className="text-2xl font-semibold text-[#FFD700] mb-6 font-serif flex items-center gap-3">
          <SparklesIcon className="h-6 w-6" />
          {language === 'tr' ? 'İsim Önerileri' : 'Name Suggestions'}
        </h3>

        <Tab.Group onChange={setSelectedCategory}>
          <Tab.List className="flex space-x-4 mb-6">
            {categories.map(({ id, icon: Icon, label }) => (
              <Tab
                key={id}
                className={({ selected }) =>
                  `flex-1 py-3 px-4 rounded-lg font-serif text-lg transition-all
                  ${selected 
                    ? 'bg-[#FFB800]/20 text-[#FFD700] border border-[#FFB800]/40' 
                    : 'text-[#FFB800]/60 hover:text-[#FFB800] border border-transparent'
                  }`
                }
              >
                <div className="flex items-center justify-center gap-2">
                  <Icon className="h-5 w-5" />
                  {label}
                </div>
              </Tab>
            ))}
          </Tab.List>

          <Tab.Panels>
            {categories.map(({ id }) => (
              <Tab.Panel
                key={id}
                className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4"
              >
                {nameCategories[id][language].map((name) => (
                  <div
                    key={name}
                    className="relative group"
                    onMouseEnter={() => setHoveredLetter(name)}
                    onMouseLeave={() => setHoveredLetter(null)}
                  >
                    <div className="p-4 rounded-lg bg-[#FFB800]/5 border border-[#FFB800]/20 backdrop-blur-sm hover:bg-[#FFB800]/10 transition-all cursor-pointer">
                      <p className="text-[#FFD700] text-lg font-serif text-center">{name}</p>
                    </div>
                    
                    {/* Enerji grafiği tooltip */}
                    {hoveredLetter === name && (
                      <div className="absolute z-50 w-64 p-4 bg-[#0A0B1A]/95 rounded-lg border border-[#FFB800]/20 backdrop-blur-xl shadow-xl -translate-y-full left-1/2 -translate-x-1/2 top-0 mt-2">
                        <p className="text-[#FFD700] text-sm mb-2 text-center font-serif">
                          {language === 'tr' ? 'Harf Enerjileri' : 'Letter Energies'}
                        </p>
                        <div className="space-y-2">
                          {calculateNameEnergy(name).map(({ letter, energy }, index) => (
                            <div key={index} className="flex items-center gap-2">
                              <span className="text-[#FFB800] w-6">{letter}</span>
                              <div className="flex-1 h-2 bg-[#FFB800]/10 rounded-full overflow-hidden">
                                <div
                                  className="h-full bg-gradient-to-r from-[#FFB800] to-[#FFD700] rounded-full transition-all duration-500"
                                  style={{ width: `${energy}%` }}
                                />
                              </div>
                              <span className="text-[#FFB800]/80 text-sm">{energy}</span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </Tab.Panel>
            ))}
          </Tab.Panels>
        </Tab.Group>
      </div>
    </div>
  );
}
