import React, { useState } from 'react';
import { motion } from 'framer-motion';

const nameCategories = {
  modern: {
    tr: ['Atlas', 'Deniz', 'Maya', 'Zeynep', 'Kuzey'],
    en: ['Atlas', 'Ocean', 'Maya', 'Zephyr', 'North']
  },
  classic: {
    tr: ['Ahmet', 'Mehmet', 'Ayşe', 'Fatma', 'Ali'],
    en: ['Alexander', 'Michael', 'Elizabeth', 'Mary', 'John']
  },
  spiritual: {
    tr: ['Aydın', 'Işık', 'Yıldız', 'Güneş', 'Ay'],
    en: ['Light', 'Star', 'Sun', 'Moon', 'Spirit']
  }
};

const letterEnergies = {
  A: 90, B: 70, C: 60, D: 80, E: 85,
  F: 65, G: 75, H: 70, I: 80, J: 60,
  K: 75, L: 70, M: 80, N: 75, O: 85,
  P: 70, Q: 60, R: 80, S: 75, T: 70,
  U: 75, V: 65, W: 70, X: 60, Y: 75,
  Z: 80
};

function NameSuggestions({ language }) {
  const [selectedCategory, setSelectedCategory] = useState('modern');
  const [hoveredName, setHoveredName] = useState(null);

  const calculateEnergy = (name) => {
    return name.split('').reduce((total, letter) => {
      return total + (letterEnergies[letter.toUpperCase()] || 0);
    }, 0) / name.length;
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.9 }}
      className="mt-12"
    >
      <h2 className="text-2xl font-cinzel mb-6 text-center">
        {language === 'tr' ? 'İsim Önerileri' : 'Name Suggestions'}
      </h2>
      
      <div className="flex justify-center gap-4 mb-8">
        {Object.keys(nameCategories).map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-4 py-2 rounded-lg transition-colors ${
              selectedCategory === category
                ? 'bg-gold-default text-midnight'
                : 'bg-midnight-light/50 border border-gold-default/30 hover:border-gold-default'
            }`}
          >
            {language === 'tr'
              ? category === 'modern'
                ? 'Modern'
                : category === 'classic'
                ? 'Klasik'
                : 'Spiritüel'
              : category.charAt(0).toUpperCase() + category.slice(1)}
          </button>
        ))}
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
        {nameCategories[selectedCategory][language].map((name) => (
          <motion.div
            key={name}
            className="relative group"
            onMouseEnter={() => setHoveredName(name)}
            onMouseLeave={() => setHoveredName(null)}
          >
            <div className="p-4 bg-midnight-light/30 backdrop-blur-sm border border-gold-default/30 rounded-lg text-center hover:border-gold-default transition-colors">
              <span className="text-lg font-cormorant">{name}</span>
            </div>
            
            {hoveredName === name && (
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="absolute -bottom-2 left-1/2 transform -translate-x-1/2 w-48 p-4 bg-midnight-light/90 backdrop-blur-sm border border-gold-default/30 rounded-lg shadow-lg"
              >
                <div className="text-sm mb-2">
                  {language === 'tr' ? 'Enerji Seviyesi' : 'Energy Level'}: {Math.round(calculateEnergy(name))}%
                </div>
                <div className="h-2 bg-midnight-light/50 rounded-full overflow-hidden">
                  <motion.div
                    initial={{ width: 0 }}
                    animate={{ width: `${calculateEnergy(name)}%` }}
                    transition={{ duration: 0.5 }}
                    className="h-full bg-gradient-to-r from-gold-default to-gold-light"
                  />
                </div>
              </motion.div>
            )}
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
}

export default NameSuggestions;
