import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const symbols = ['Σ', 'π', '∞', '∆', 'Ω', '√'];

export default function IntroAnimation({ onComplete }) {
  const [showSymbols, setShowSymbols] = useState(true);
  const [playSound] = useState(new Audio('/assets/magical-chime.mp3'));

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowSymbols(false);
      playSound.play();
      setTimeout(onComplete, 1000);
    }, 3000);

    return () => clearTimeout(timer);
  }, [onComplete, playSound]);

  return (
    <AnimatePresence>
      {showSymbols && (
        <motion.div
          className="fixed inset-0 bg-[#0A0B1A] z-50 flex items-center justify-center"
          initial={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="relative">
            {/* Merkezdeki ana sembol */}
            <motion.div
              className="text-[#FFD700] text-8xl font-serif absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2"
              initial={{ scale: 0, rotate: -180 }}
              animate={{ scale: 1, rotate: 0 }}
              transition={{ duration: 1, ease: "easeOut" }}
            >
              Ω
            </motion.div>

            {/* Dönen semboller */}
            <div className="relative w-40 h-40">
              {symbols.map((symbol, index) => (
                <motion.div
                  key={symbol}
                  className="absolute left-1/2 top-1/2 text-3xl text-[#FFB800]"
                  initial={{ 
                    x: 0, 
                    y: 0, 
                    opacity: 0,
                    scale: 0.5
                  }}
                  animate={{ 
                    x: Math.cos(index * (Math.PI / 3)) * 80,
                    y: Math.sin(index * (Math.PI / 3)) * 80,
                    opacity: 1,
                    scale: 1
                  }}
                  transition={{
                    duration: 1,
                    delay: index * 0.2,
                    ease: "easeOut"
                  }}
                >
                  {symbol}
                </motion.div>
              ))}
            </div>

            {/* Işık efekti */}
            <motion.div
              className="absolute inset-0 bg-gradient-radial from-[#FFB800]/20 to-transparent"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1, delay: 0.5 }}
            />
          </div>

          <motion.h1
            className="absolute bottom-20 text-[#FFD700] text-4xl font-serif"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 1.5 }}
          >
            Nomen Est Omen
          </motion.h1>
        </motion.div>
      )}
    </AnimatePresence>
  );
}
