import React, { useContext } from 'react';
import { LanguageContext } from '../contexts/LanguageContext';
import { Link } from 'react-router-dom';
import ZodiacSymbol from './ZodiacSymbol';

function Header({ playClickSound, toggleSound, isSoundEnabled }) {
  const { language, setLanguage } = useContext(LanguageContext);

  const translations = {
    en: {
      title: 'Nomen Est Omen',
      subtitle: 'The Name is a Sign',
      nameAnalyzer: 'Name Analyzer',
      horoscope: 'Horoscope',
      about: 'About'
    },
    tr: {
      title: 'Nomen Est Omen',
      subtitle: 'Ä°sim Bir Ä°ÅŸarettir',
      nameAnalyzer: 'Ä°sim Analizi',
      horoscope: 'BurÃ§ YorumlarÄ±',
      about: 'HakkÄ±nda'
    }
  };

  const handleLanguageToggle = () => {
    setLanguage(language === 'en' ? 'tr' : 'en');
    if (playClickSound) playClickSound();
  };

  const handleLinkClick = () => {
    if (playClickSound) playClickSound();
  };

  return (
    <header className="bg-midnight py-3 px-4 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        <div className="flex items-center space-x-2">
          <ZodiacSymbol sign="aries" size="small" className="zodiac-symbol" />
          <div className="fadeIn">
            <h1 className="text-xl md:text-2xl font-serif text-gold">{translations[language].title}</h1>
            <p className="text-xs md:text-sm text-gray-400 italic">{translations[language].subtitle}</p>
          </div>
        </div>
        
        <nav className="flex items-center space-x-3 md:space-x-6">
          <Link to="/" className="text-sm md:text-base text-gray-300 hover:text-gold transition-colors tab-transition" onClick={handleLinkClick}>
            {translations[language].nameAnalyzer}
          </Link>
          <Link to="/horoscope" className="text-sm md:text-base text-gray-300 hover:text-gold transition-colors tab-transition" onClick={handleLinkClick}>
            {translations[language].horoscope}
          </Link>
          <Link to="/about" className="text-sm md:text-base text-gray-300 hover:text-gold transition-colors tab-transition" onClick={handleLinkClick}>
            {translations[language].about}
          </Link>
          
          <button 
            onClick={handleLanguageToggle} 
            className="text-sm md:text-base bg-midnight border border-gold text-gold px-2 py-1 rounded hover:bg-gold hover:text-midnight transition-colors glowPulse"
          >
            {language === 'en' ? 'TR' : 'EN'}
          </button>
          
          <button 
            onClick={toggleSound} 
            className="text-sm md:text-base bg-midnight border border-gold text-gold px-2 py-1 rounded hover:bg-gold hover:text-midnight transition-colors glowPulse"
            title={language === 'en' ? (isSoundEnabled ? 'Mute' : 'Unmute') : (isSoundEnabled ? 'Sessiz' : 'Ses AÃ§')}
          >
            {isSoundEnabled ? 'ðŸ”Š' : 'ðŸ”‡'}
          </button>
        </nav>
      </div>
    </header>
  );
}

export default Header;
