  g: [9, 4],
  h: [3, 1],
  ı: [4, 8],
  i: [7, 5],
  j: [2, 6],
  k: [1, 5],
  l: [6, 9],
  m: [3, 5],
  n: [2, 9],
  o: [9, 1],
  ö: [6, 4],
  p: [3, 7],
  r: [4, 9],
  s: [3, 1],
  ş: [4, 2],
  t: [5, 8],
  u: [2, 7],
  ü: [1, 3],
  v: [6, 8],
  y: [5, 7],
  z: [2, 4]
};

const NameAnalysisBoxes = ({ name, language }) => {
  // Eğer isim yoksa boş döndür
  if (!name) return null;

  // İlk harfi küçük harfe çevir
  const firstLetter = name.charAt(0).toLowerCase();
  
  // İlk harfe göre verileri al
  const compatibleZodiacs = nameZodiacData[firstLetter] || ['Koç', 'Aslan'];
  const element = nameElementData[firstLetter] || 'Ateş';
  const luckyColors = nameLuckyColors[firstLetter] || ['Kırmızı', 'Altın'];
  const luckyNumbers = nameLuckyNumbers[firstLetter] || [1, 7];

  // Dil çevirileri
  const translations = {
    tr: {
      zodiacTitle: 'Uyumlu Burçlar',
      elementTitle: 'Element',
      luckyColorsTitle: 'Şans Renkleri',
      luckyNumbersTitle: 'Şans Sayıları',
      soulNumberTitle: 'Ruh Sayısı',
      fireDesc: 'Ateş elementi güçlü, tutkulu ve dinamik bir enerji taşır.',
      earthDesc: 'Toprak elementi pratik, kararlı ve güvenilir bir yapı sağlar.',
      airDesc: 'Hava elementi iletişimci, entelektüel ve sosyal özellikler verir.',
      waterDesc: 'Su elementi duygusal, sezgisel ve derin bir anlayış sunar.'
    },
    en: {
      zodiacTitle: 'Compatible Zodiacs',
      elementTitle: 'Element',
      luckyColorsTitle: 'Lucky Colors',
      luckyNumbersTitle: 'Lucky Numbers',
      soulNumberTitle: 'Soul Number',
      fireDesc: 'Fire element carries a strong, passionate and dynamic energy.',
      earthDesc: 'Earth element provides a practical, stable and reliable structure.',
      airDesc: 'Air element gives communicative, intellectual and social traits.',
      waterDesc: 'Water element offers emotional, intuitive and deep understanding.'
    }
  };

  // Dil seçimi
  const t = translations[language] || translations.tr;

  // İsmin harflerinin sayısal değerlerinin toplamından ruh sayısını hesapla (basit örnek)
  const calculateSoulNumber = (name) => {
    const letterValues = {
      a: 1, b: 2, c: 3, ç: 4, d: 5, e: 6, f: 7, g: 8, h: 9, ı: 1, i: 1, 
      j: 1, k: 2, l: 3, m: 4, n: 5, o: 6, ö: 7, p: 8, r: 9, s: 1, ş: 2, 
      t: 3, u: 4, ü: 5, v: 6, y: 7, z: 8
    };
    
    let sum = 0;
    for (let i = 0; i < name.length; i++) {
      const letter = name.charAt(i).toLowerCase();
      sum += letterValues[letter] || 0;
    }
    
    // Toplamı tek basamaklı bir sayıya indirge
    while (sum > 9) {
      sum = sum.toString().split('').reduce((a, b) => parseInt(a) + parseInt(b), 0);
    }
    
    return sum;
  };

  const soulNumber = calculateSoulNumber(name);

  // Element açıklaması
  const getElementDescription = (element) => {
    switch(element) {
      case 'Ateş': return t.fireDesc;
      case 'Toprak': return t.earthDesc;
      case 'Hava': return t.airDesc;
      case 'Su': return t.waterDesc;
      default: return t.fireDesc;
    }
  };

  const elementDesc = getElementDescription(element);

  return (
    <div className="mt-8">
      <h3 className="text-2xl font-cinzel text-gold-default text-center mb-6">{name} {language === 'tr' ? 'İçin Özel Analizler' : 'Special Analyses'}</h3>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Uyumlu Burçlar Kutusu */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.1 }}
          className="bg-midnight-light/30 backdrop-blur-sm p-6 rounded-lg border border-gold-default/30 hover:border-gold-default/70 transition-all duration-300 hover:shadow-lg hover:shadow-gold-default/10"
        >
          <h4 className="text-xl font-cinzel text-gold-default mb-3">{t.zodiacTitle}</h4>
          <div className="flex flex-wrap gap-2 mb-4">
            {compatibleZodiacs.map((zodiac, index) => (
              <span 
                key={index}
                className="bg-gold-default/20 text-gold-light px-3 py-1 rounded-full text-sm"
              >
                {zodiac}
              </span>
            ))}
          </div>
          <p className="text-gold-light/80 text-sm">
            {language === 'tr' 
              ? `${name} ismi ${compatibleZodiacs.join(' ve ')} burçları ile uyumludur.`
              : `The name ${name} is compatible with ${compatibleZodiacs.join(' and ')} zodiacs.`
            }
          </p>
        </motion.div>

        {/* Element Kutusu */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="bg-midnight-light/30 backdrop-blur-sm p-6 rounded-lg border border-gold-default/30 hover:border-gold-default/70 transition-all duration-300 hover:shadow-lg hover:shadow-gold-default/10"
        >
          <h4 className="text-xl font-cinzel text-gold-default mb-3">{t.elementTitle}</h4>
          <div className="flex items-center justify-center mb-4">
            <div className="w-16 h-16 rounded-full bg-gold-default/30 flex items-center justify-center text-2xl font-bold text-gold-default">
              {element === 'Ateş' ? '🔥' : element === 'Su' ? '💧' : element === 'Toprak' ? '🌱' : '💨'}
            </div>
          </div>
          <p className="text-gold-light/80 text-sm">
            {language === 'tr' 
              ? `${name} ismi ${element} elementine bağlıdır. ${elementDesc}`
              : `The name ${name} is linked to the ${element} element. ${elementDesc}`
            }
          </p>
        </motion.div>

        {/* Şans Renkleri Kutusu */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.3 }}
          className="bg-midnight-light/30 backdrop-blur-sm p-6 rounded-lg border border-gold-default/30 hover:border-gold-default/70 transition-all duration-300 hover:shadow-lg hover:shadow-gold-default/10"
        >
          <h4 className="text-xl font-cinzel text-gold-default mb-3">{t.luckyColorsTitle}</h4>
          <div className="flex flex-wrap gap-2 mb-4">
            {luckyColors.map((color, index) => (
              <span 
                key={index}
                className="bg-gold-default/20 text-gold-light px-3 py-1 rounded-full text-sm"
              >
                {color}
              </span>
            ))}
          </div>
          <p className="text-gold-light/80 text-sm">
            {language === 'tr' 
              ? `${name} isminin şans renkleri ${luckyColors.join(' ve ')}'dir.`
              : `The lucky colors for the name ${name} are ${luckyColors.join(' and ')}.`
            }
          </p>
        </motion.div>

        {/* Şans Sayıları Kutusu */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.4 }}
          className="bg-midnight-light/30 backdrop-blur-sm p-6 rounded-lg border border-gold-default/30 hover:border-gold-default/70 transition-all duration-300 hover:shadow-lg hover:shadow-gold-default/10"
        >
          <h4 className="text-xl font-cinzel text-gold-default mb-3">{t.luckyNumbersTitle}</h4>
          <div className="flex gap-4 justify-center mb-4">
            {luckyNumbers.map((number, index) => (
              <div 
                key={index}
                className="w-10 h-10 rounded-full bg-gold-default/30 flex items-center justify-center text-xl font-bold text-gold-default"
              >
                {number}
              </div>
            ))}
          </div>
          <p className="text-gold-light/80 text-sm">
            {language === 'tr' 
              ? `${name} isminin şans sayıları ${luckyNumbers.join(' ve ')}'dir.`
              : `The lucky numbers for the name ${name} are ${luckyNumbers.join(' and ')}.`
            }
          </p>
        </motion.div>

        {/* Ruh Sayısı Kutusu */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.5 }}
          className="bg-midnight-light/30 backdrop-blur-sm p-6 rounded-lg border border-gold-default/30 hover:border-gold-default/70 transition-all duration-300 hover:shadow-lg hover:shadow-gold-default/10"
        >
          <h4 className="text-xl font-cinzel text-gold-default mb-3">{t.soulNumberTitle}</h4>
          <div className="flex justify-center mb-4">
            <div className="w-16 h-16 rounded-full bg-gold-default/30 flex items-center justify-center text-2xl font-bold text-gold-default">
              {soulNumber}
            </div>
          </div>
          <p className="text-gold-light/80 text-sm">
            {language === 'tr' 
              ? `${name} isminin ruh sayısı ${soulNumber}'dir. Bu sayı isminizdeki harflerin numerolojik değerlerinin toplamını temsil eder.`
              : `The soul number for the name ${name} is ${soulNumber}. This number represents the sum of the numerological values of the letters in your name.`
            }
          </p>
        </motion.div>
      </div>
    </div>
  );
};

export default NameAnalysisBoxes;
